var switch_states_old = flow.get('switch_states')  || [];
var switch_states = flow.get('switch_states')  || [];
var switch_power_levels = flow.get('switch_power_levels')  || [];
var pin_states_old = flow.get('pin_states_old')  || [];
var pin_states = flow.get('pin_states')  || [];
var pin_state_change_times = flow.get('pin_state_change_times') || [];

// Update old switch states with current switch states 
flow.set('switch_states_old', switch_states_old);

// See if pin states have changed
for (var i=0; i<pin_states.length; i++)
{
    for (var j=0; j<pin_states[i].length; j++)
    {
        // State has changed
        if (pin_states_old[i][j] != pin_states[i][j])
        {
            var timeSinceLastChange = (Date.now() - pin_state_change_times[i][j])
            node.warn("State change on " + i + "-" + j + "; diff " + timeSinceLastChange);
            pin_state_change_times[i][j] = Date.now();

            // Fix-up switch states        
            if (pin_states[i][j] > 0) {
                node.warn("pin ON");
            }
            else {
                node.warn("pin OFF");
                // Quick press to toggle on/off
                if (timeSinceLastChange <= 1000)
                {
                    // Toggle switch on/off
                    if (switch_states[i][j])
                    {
                        //node.warn("off");
                        switch_states[i][j] = false;
                    }
                    else
                    {
                        //node.warn("on");
                        switch_states[i][j] =true;
                    }
                }
                // Change power level
                else if (timeSinceLastChange > 1000 && timeSinceLastChange <= 5000)
                {
                    switch_states[i][j] = true;
                    switch_power_levels[i][j] = timeSinceLastChange/5000 * 100;
                }
                else if (timeSinceLastChange > 5000) {
                    switch_states[i][j] = true;
                    switch_power_levels[i][j] = 100;
                }


                node.warn("switch state:  " + switch_states[i][j]);
                node.warn("timeSinceLastChange " + timeSinceLastChange);
                node.warn("Power Level: " + switch_power_levels[i][j]);
            }

            var newMsg = {};
            newMsg.topic = "switch_state";
            if (switch_states[i][j])
            {
                newMsg.payload = switch_power_levels[i][j];
            }
            else {
                newMsg.payload = 0;
            }
            newMsg.mcp = i;
            newMsg.switch = j;
            node.send(newMsg);
        }

    }
}

flow.set('switch_states', switch_states);
flow.set('switch_power_levels', switch_power_levels);
flow.set('pin_state_change_times', pin_state_change_times);

return msg;

