// Code added here will be run once
// whenever the node is started.
var rows = 4;
var columns = 16;

var switch_states = [];
var switch_power_levels = [];
var pin_state_change_times = [];

fill2DimensionsArray(switch_states, rows, columns, 0);
fill2DimensionsArray(switch_power_levels, rows, columns, 100);
fill2DimensionsArray(pin_state_change_times, rows, columns, 0);

flow.set('switch_states', switch_states);
flow.set('switch_power_levels', switch_power_levels);
flow.set('pin_state_change_times', pin_state_change_times);

function fill2DimensionsArray(arr, rows, columns, val){
    for (var i = 0; i < rows; i++) {
        arr.push([0])
        for (var j = 0; j < columns; j++) {
            arr[i][j] = val;        }
    }
}
